// ‚îÄ‚îÄ API (bulletproof timeout via Promise.race) ‚îÄ‚îÄ
async function callClaude(sysPrompt, userPrompt, timeoutMs = 60000, useSearch = true) {
  const timeoutPromise = new Promise((resolve) => {
    setTimeout(() => resolve("__TIMEOUT__"), timeoutMs);
  });

  const fetchPromise = (async () => {
    try {
      const body = {
        model: "claude-sonnet-4-20250514",
        max_tokens: 1200,
        system: sysPrompt,
        messages: [{ role: "user", content: userPrompt }],
      };
      if (useSearch) {
        body.tools = [{ type: "web_search_20250305", name: "web_search" }];
      }
      const res = await fetch("https://api.anthropic.com/v1/messages", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });
      const data = await res.json();
      return (data.content || []).filter((b) => b.type === "text").map((b) => b.text).join("\n");
    } catch (e) {
      console.error("API error:", e);
      return null;
    }
  })();

  return Promise.race([fetchPromise, timeoutPromise]);
}

function parseJSON(text) {
  if (!text || text === "__TIMEOUT__") return null;
  try {
    const arr = text.match(/\[[\s\S]*\]/);
    if (arr) return JSON.parse(arr[0]);
    const obj = text.match(/\{[\s\S]*\}/);
    if (obj) return JSON.parse(obj[0]);
  } catch { /* */ }
  try { return JSON.parse(text.replace(/```json|```/g, "").trim()); } catch { /* */ }
  return null;
}

